CREATE DATABASE cs348_project_db;
USE cs348_project_db;

-- Create user table
DROP TABLE IF EXISTS user;
CREATE TABLE user (
    username VARCHAR(255) NOT NULL,
    password VARCHAR(255) NOT NULL, 
    pfp VARCHAR(255) NOT NULL,
    PRIMARY KEY (username)
);

-- insert a user


-- Create top100_cities Table and populate sample data
DROP TABLE IF EXISTS top100_cities;
CREATE TABLE top100_cities(
   ranking                INTEGER  NOT NULL PRIMARY KEY 
  ,city                   VARCHAR(17) NOT NULL
  ,country                VARCHAR(20) NOT NULL
  ,economic_performance   INTEGER  NOT NULL
  ,tourism_performance    INTEGER  NOT NULL
  ,tourism_policy         INTEGER  NOT NULL
  ,tourism_infrastructure INTEGER  NOT NULL
  ,health_safety          INTEGER  NOT NULL
  ,sustainability         INTEGER  NOT NULL
  ,description            VARCHAR(1059) NOT NULL
);

-- INDICES
CREATE INDEX idx_city ON top100_cities(city);
CREATE INDEX idx_country ON top100_cities(country);
CREATE INDEX idx_economic_performance ON top100_cities(economic_performance);
CREATE INDEX idx_tourism_performance ON top100_cities(tourism_performance);
CREATE INDEX idx_tourism_policy ON top100_cities(tourism_policy);
CREATE INDEX idx_tourism_infrastructure ON top100_cities(tourism_infrastructure);
CREATE INDEX idx_health_safety ON top100_cities(health_safety);
CREATE INDEX idx_sustainability ON top100_cities(sustainability);


-- Create and populate top100_cities_weather
-- Create weather table
DROP TABLE IF EXISTS top100_cities_weather;
CREATE TABLE top100_cities_weather(
   city                VARCHAR(17) NOT NULL
  ,temperature_celsius DECIMAL(20,16) NOT NULL
  ,wind_speed_ms       DECIMAL(10,5) NOT NULL
  ,latitude            DECIMAL(10,5) NOT NULL
  ,longitude           DECIMAL(10,5) NOT NULL
  ,description         VARCHAR(20) NOT NULL
  ,country             VARCHAR(20) NOT NULL
  ,PRIMARY KEY(city, country)
);


