-- Create favourite cities table
DROP TABLE IF EXISTS  favourite_cities;
CREATE TABLE favourite_cities (
    username VARCHAR(255) NOT NULL,
    city_id INT NOT NULL,
    FOREIGN KEY (city_id) REFERENCES top100_cities(ranking),
    PRIMARY KEY (username, city_id)
);

-- Sample favourite cities data


-- Create most_favourited table
DROP TABLE IF EXISTS most_favourited;
CREATE TABLE most_favourited (
    city_id INT PRIMARY KEY,
    city_name VARCHAR(255),
    country VARCHAR(255),
    total_favourites INT DEFAULT 0,
    last_updated TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


-- populate most_favourited table
SELECT
    t.ranking AS city_id,   
    t.city AS city_name,
    t.country,
    COUNT(fc.username) AS total_favourites
FROM
    favourite_cities fc
JOIN
    top100_cities t ON fc.city_id = t.ranking 
GROUP BY
    t.ranking, t.city, t.country;
    
    
-- create event to reset the table every month
DROP EVENT IF EXISTS reset_monthly_favourites;

CREATE EVENT reset_monthly_favourites
ON SCHEDULE EVERY 1 MONTH
STARTS TIMESTAMP(CURRENT_DATE + INTERVAL 1 MONTH)
DO
    UPDATE most_favourited
    SET total_favourites = 0, last_updated = CURRENT_TIMESTAMP;
    

-- create the reviews table
-- Create review table
DROP TABLE IF EXISTS reviews;
CREATE TABLE reviews (
    review_id INT AUTO_INCREMENT PRIMARY KEY,
    city_id INT NOT NULL,
    username VARCHAR(255) NOT NULL,
    rating INT CHECK (rating BETWEEN 1 AND 5),
    review_text TEXT,
    FOREIGN KEY (city_id) REFERENCES top100_cities(ranking)
);

-- Create a trigger to handle user deletion
DELIMITER //
CREATE TRIGGER after_user_delete
AFTER DELETE ON user
FOR EACH ROW
BEGIN
    UPDATE reviews
    SET username = 'Deleted User'
    WHERE username = OLD.username;
END //
DELIMITER ;

